package com.kddi.remoteSimlock.manager;

import android.util.Log;
import java.util.List;

import com.kddi.remoteSimlock.IKDDIRemoteSimlockServiceCallback;
import com.kddi.remoteSimlock.SimLockData;

/**
 * KDDIRemoteSimlockManagerCallback class for appliction to receive Asynchronous response. (Note:
 * Application shall extend this class and override the APIs of this class)
 */
public class KDDIRemoteSimlockManagerCallback extends IKDDIRemoteSimlockServiceCallback.Stub {
    private static final String TAG = "KDDIRemoteSimlockManagerCallback:";

    /**
    * Remote Lock/Unlock operation response.
    *
    * @param token
    * Identifier for the client to match the request.
    *
    * @param responseCode
    * SIMLOCK_RESP_SUCCESS = 0;
    * SIMLOCK_RESP_FAIL = 1;
    *
    * @param simlockResponse
    * Status Response message of the remote lock/unlock operation generated by Network Lock Module
    */
    @Override
    public void setSimLockPolicyDataResponse(int token, int responseCode,
            int soltId) {
        loge("default setSimLockPolicyDataResponse, user should override this API");
    }

    /**
    * Highest major and minor version of incoming blob (Response message) that is supported by
    * Network Lock Module.
    *
    * @param token
    * Identifier for the client to match the request.
    *
    * @param responseCode
    * SIMLOCK_RESP_SUCCESS = 0;
    * SIMLOCK_RESP_FAIL = 1;
    *
    * @param majorVersion
    * Highest major version of the incoming blob Message that is supposed by Network Lock Module
    *
    * @param minorVersion
    * Highest minor version of the incoming blob Message that is supposed by Network Lock Module
    */
    @Override
    public void getAllowedSimLockDataResponse(int token, int responseCode, int soltId,
            List<SimLockData> simLockData) {
        loge("default getAllowedSimLockDataResponse, user should override this API");
    }

    /**
    * Highest major and minor version of incoming blob (Response message) that is supported by
    * Network Lock Module.
    *
    * @param token
    * Identifier for the client to match the request.
    *
    * @param responseCode
    * SIMLOCK_RESP_SUCCESS = 0;
    * SIMLOCK_RESP_FAIL = 1;
    *
    * @param majorVersion
    * Highest major version of the incoming blob Message that is supposed by Network Lock Module
    *
    * @param minorVersion
    * Highest minor version of the incoming blob Message that is supposed by Network Lock Module
    */
    @Override
    public void getExcludedSimLockDataResponse(int token, int responseCode, int soltId,
            List<SimLockData> simLockData) {
        loge("default getExcludedSimLockDataResponse, user should override this API");
    }

    private void loge(String s) {
        Log.e(TAG, "[RSU-SIMLOCK] " + s);
    }
}
