#ifndef __HAL_CACHE_INTERNAL_H__
#define __HAL_CACHE_INTERNAL_H__

#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <xtensa/core-macros.h>
#include "hal_cache.h"
#include "FreeRTOS.h"
#include "task.h"

#define CACHE_LINE_SIZE             128
#define CACHE_LINE_MASK             (CACHE_LINE_SIZE - 1)
#define PREFETCH_MODE_COUNT         16
#define PREFETCH_MODE_MASK          (PREFETCH_MODE_COUNT - 1)
#define INSTRUCTION_PREFETCH_SHIFT  4
#define DATA_PREFETCH_SHIFT         0
#define INSTRUCTION_PREFETCH_MASK   (PREFETCH_MODE_MASK << INSTRUCTION_PREFETCH_SHIFT)
#define DATA_PREFETCH_MASK          (PREFETCH_MODE_MASK << DATA_PREFETCH_SHIFT)

#define DEFAULT_CACHE_PREFETCH      0x44    /* = (HAL_CACHE_PREFETCH_MODE_MEDIUM_USE_TABLE << INSTRUCTION_PREFETCH_SHIFT) | (HAL_CACHE_PREFETCH_MODE_MEDIUM_USE_TABLE << DATA_PREFETCH_SHIFT) */
#define DEFAULT_ICACHE_WAY          2
#define DEFAULT_DCACHE_WAY          4


hal_cache_prefetch_mode_t prefetch_value_to_mode (int32_t value);
int32_t prefetch_mode_to_value (hal_cache_prefetch_mode_t mode);

#endif /* __HAL_CACHE_INTERNAL_H__ */
