#include "hal_cache_internal.h"

static const hal_cache_prefetch_mode_t prefetch_mode_table[PREFETCH_MODE_COUNT] = {
    HAL_CACHE_PREFETCH_MODE_OFF,                /* 0x0 */
    HAL_CACHE_PREFETCH_MODE_LOW_USE_TABLE,      /* 0x1 */
    HAL_CACHE_PREFETCH_MODE_OFF,                /* 0x2 */
    HAL_CACHE_PREFETCH_MODE_LOW_ANY_MISS,       /* 0x3 */
    HAL_CACHE_PREFETCH_MODE_MEDIUM_USE_TABLE,   /* 0x4 */
    HAL_CACHE_PREFETCH_MODE_MEDIUM_NO_TABLE,    /* 0x5 */
    HAL_CACHE_PREFETCH_MODE_OFF,                /* 0x6 */
    HAL_CACHE_PREFETCH_MODE_OFF,                /* 0x7 */
    HAL_CACHE_PREFETCH_MODE_HIGH,               /* 0x8 */
    HAL_CACHE_PREFETCH_MODE_OFF,                /* 0x9 */
    HAL_CACHE_PREFETCH_MODE_OFF,                /* 0xA */
    HAL_CACHE_PREFETCH_MODE_OFF,                /* 0xB */
    HAL_CACHE_PREFETCH_MODE_OFF,                /* 0xC */
    HAL_CACHE_PREFETCH_MODE_OFF,                /* 0xD */
    HAL_CACHE_PREFETCH_MODE_OFF,                /* 0xE */
    HAL_CACHE_PREFETCH_MODE_OFF                 /* 0xF */
};

int32_t prefetch_mode_to_value (hal_cache_prefetch_mode_t mode)
{
    int32_t value = (int32_t)mode & PREFETCH_MODE_MASK;
    return value;
}

hal_cache_prefetch_mode_t prefetch_value_to_mode (int32_t value)
{
    int32_t index = value & PREFETCH_MODE_MASK;
    hal_cache_prefetch_mode_t mode = prefetch_mode_table[index];
    return mode;
}
